import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={LeagueSettingsPage} />
      <Route path="/draft" component={DraftRoom} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
