import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle, DialogFooter } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardContent } from '@/components/ui/card';
import { useAppContext } from '@/lib/app-context';
import { SAMPLE_PROJECTIONS, SAMPLE_LEAGUE_SETTINGS, SAMPLE_SCORING_FORMAT, SAMPLE_VALUE_SETTINGS } from '@/lib/sample-data';
import { calculatePlayerValues } from '@/lib/calculations';
import { Settings, Upload, Calculator, Gavel, Sparkles } from 'lucide-react';

const ONBOARDING_KEY = 'fantasy-baseball-onboarding-completed';

export function WelcomeDialog() {
  const [isOpen, setIsOpen] = useState(false);
  const [step, setStep] = useState<'welcome' | 'features'>('welcome');

  const { 
    setLeagueSettings, 
    setScoringFormat, 
    setValueCalculationSettings,
    setPlayerProjections, 
    setPlayerValues,
    setDraftState,
    setTargetedPlayerIds,
    playerValues 
  } = useAppContext();

  useEffect(() => {
    const onboardingCompleted = localStorage.getItem(ONBOARDING_KEY);
    const hasData = playerValues.length > 0;

    if (!onboardingCompleted && !hasData) {
      setIsOpen(true);
    }
  }, [playerValues]);

  const handleDismiss = () => {
    localStorage.setItem(ONBOARDING_KEY, 'true');
    setIsOpen(false);
  };

  const handleLoadSampleData = () => {
    setLeagueSettings(SAMPLE_LEAGUE_SETTINGS);
    setScoringFormat(SAMPLE_SCORING_FORMAT);
    setValueCalculationSettings(SAMPLE_VALUE_SETTINGS);
    setPlayerProjections(SAMPLE_PROJECTIONS);

    const calculatedValues = calculatePlayerValues(
      SAMPLE_PROJECTIONS,
      SAMPLE_LEAGUE_SETTINGS,
      SAMPLE_SCORING_FORMAT,
      SAMPLE_VALUE_SETTINGS
    );
    setPlayerValues(calculatedValues);

    setDraftState({
      picks: [],
      currentInflationRate: 0,
      totalBudgetSpent: 0,
      totalPlayersAvailable: calculatedValues.length,
      totalPlayersDrafted: 0,
    });

    setTargetedPlayerIds([]);

    localStorage.setItem(ONBOARDING_KEY, 'true');
    setIsOpen(false);
  };

  const features = [
    {
      icon: Settings,
      title: 'League Configuration',
      description: 'Set up team count, budget, roster requirements, and position slots',
    },
    {
      icon: Upload,
      title: 'CSV Projection Upload',
      description: 'Import player projections from any source with smart column mapping',
    },
    {
      icon: Calculator,
      title: 'Auction Values',
      description: 'Calculate dollar values using SGP or Z-Score methods with positional scarcity',
    },
    {
      icon: Gavel,
      title: 'Live Draft Room',
      description: 'Track picks in real-time with automatic inflation adjustments',
    },
  ];

  return (
    <Dialog open={isOpen} onOpenChange={setIsOpen}>
      <DialogContent className="sm:max-w-lg">
        {step === 'welcome' ? (
          <>
            <DialogHeader>
              <DialogTitle className="font-display text-2xl tracking-tight flex items-center gap-2">
                <Sparkles className="h-6 w-6 text-baseball-gold" />
                Welcome to Auction Values
              </DialogTitle>
              <DialogDescription className="text-base pt-2">
                Your complete fantasy baseball auction calculator for draft day success
              </DialogDescription>
            </DialogHeader>

            <div className="py-4 space-y-4">
              <p className="text-sm text-muted-foreground">
                Calculate accurate auction values based on your league's specific settings,
                track live draft inflation, and export printable cheat sheets.
              </p>

              <div className="flex justify-center py-2">
                <Button
                  variant="outline"
                  onClick={() => setStep('features')}
                  className="text-sm"
                  data-testid="button-learn-more"
                >
                  Learn about features
                </Button>
              </div>
            </div>

            <DialogFooter className="flex-col sm:flex-row gap-2">
              <Button variant="outline" onClick={handleDismiss} data-testid="button-skip-onboarding">
                Skip, I know what I'm doing
              </Button>
              <Button onClick={handleLoadSampleData} data-testid="button-load-sample">
                Load Sample Data
              </Button>
            </DialogFooter>
          </>
        ) : (
          <>
            <DialogHeader>
              <DialogTitle className="font-display text-xl tracking-tight">
                Key Features
              </DialogTitle>
            </DialogHeader>

            <div className="grid grid-cols-2 gap-3 py-4">
              {features.map((feature, idx) => {
                const Icon = feature.icon;
                return (
                  <Card key={idx} className="border-card-border hover-elevate">
                    <CardContent className="p-4">
                      <Icon className="h-5 w-5 text-baseball-navy mb-2" />
                      <h4 className="font-semibold text-sm mb-1">{feature.title}</h4>
                      <p className="text-xs text-muted-foreground">{feature.description}</p>
                    </CardContent>
                  </Card>
                );
              })}
            </div>

            <DialogFooter className="flex-col sm:flex-row gap-2">
              <Button variant="outline" onClick={() => setStep('welcome')} data-testid="button-back">
                Back
              </Button>
              <Button onClick={handleLoadSampleData} data-testid="button-try-sample">
                Try with Sample Data
              </Button>
            </DialogFooter>
          </>
        )}
      </DialogContent>
    </Dialog>
  );
}

client/src/lib/mlb-api.ts.content:
const MLB_STATS_API_BASE = 'https://statsapi.mlb.com/api/v1';

interface MlbPerson {
  id: number;
  fullName: string;
  primaryPosition?: {
    code: string;
    name: string;
    type: string;
    abbreviation: string;
  };
}

interface MlbApiResponse {
  people?: MlbPerson[];
}

const POSITION_MAP: Record<string, string> = {
  '1': 'P',
  '2': 'C',
  '3': '1B',
  '4': '2B',
  '5': '3B',
  '6': 'SS',
  '7': 'OF',
  '8': 'OF',
  '9': 'OF',
  '10': 'DH',
  'P': 'P',
  'C': 'C',
  'D': 'DH',
  'O': 'OF',
  'Y': 'TWP',
};

export async function fetchPlayerPosition(mlbamId: string): Promise<string[]> {
  try {
    const response = await fetch(`${MLB_STATS_API_BASE}/people/${mlbamId}`);
    if (!response.ok) {
      console.warn(`Failed to fetch player ${mlbamId}: ${response.status}`);
      return ['UTIL'];
    }

    const data: MlbApiResponse = await response.json();
    const person = data.people?.[0];

    if (!person?.primaryPosition) {
      return ['UTIL'];
    }

    const posCode = person.primaryPosition.code;
    const posAbbrev = person.primaryPosition.abbreviation;

    if (posAbbrev && ['C', '1B', '2B', '3B', 'SS', 'SP', 'RP', 'P', 'OF', 'DH'].includes(posAbbrev)) {
      if (posAbbrev === 'P') {
        return ['SP', 'RP'];
      }
      return [posAbbrev];
    }

    const mappedPos = POSITION_MAP[posCode];
    if (mappedPos) {
      return [mappedPos];
    }

    return ['UTIL'];
  } catch (error) {
    console.error(`Error fetching position for player ${mlbamId}:`, error);
    return ['UTIL'];
  }
}

export async function fetchMultiplePlayerPositions(
  mlbamIds: string[],
  onProgress?: (completed: number, total: number) => void
): Promise<Map<string, string[]>> {
  const results = new Map<string, string[]>();
  const batchSize = 10;
  const delay = 100;

  for (let i = 0; i < mlbamIds.length; i += batchSize) {
    const batch = mlbamIds.slice(i, i + batchSize);

    const promises = batch.map(async (id) => {
      const positions = await fetchPlayerPosition(id);
      return { id, positions };
    });

    const batchResults = await Promise.all(promises);

    for (const { id, positions } of batchResults) {
      results.set(id, positions);
    }

    if (onProgress) {
      onProgress(Math.min(i + batchSize, mlbamIds.length), mlbamIds.length);
    }

    if (i + batchSize < mlbamIds.length) {
      await new Promise(resolve => setTimeout(resolve, delay));
    }
  }

  return results;
}

client/src/lib/position-lookup.ts.content:
import Papa from 'papaparse';
import positionCsvUrl from '@assets/mlb_player_positions.csv?url';

interface PlayerPositionRecord {
  mlbamId: string;
  name: string;
  stdPos: string;
  yahooPos: string;
}

let positionCache: Map<string, string[]> | null = null;
let loadingPromise: Promise<Map<string, string[]>> | null = null;

function parsePositionString(posStr: string): string[] {
  if (!posStr || posStr.trim() === '') return ['UTIL'];

  const positions = posStr
    .split(/[,/]/)
    .map(p => p.trim())
    .filter(p => p.length > 0 && p !== 'P');

  if (positions.length === 0) {
    if (posStr.includes('P')) {
      return ['SP', 'RP'];
    }
    return ['UTIL'];
  }

  return positions;
}

async function loadPositionData(): Promise<Map<string, string[]>> {
  if (positionCache) {
    return positionCache;
  }

  if (loadingPromise) {
    return loadingPromise;
  }

  loadingPromise = new Promise<Map<string, string[]>>((resolve, reject) => {
    fetch(positionCsvUrl)
      .then(response => response.text())
      .then(csvText => {
        Papa.parse<string[]>(csvText, {
          complete: (results) => {
            const map = new Map<string, string[]>();

            if (results.data.length < 2) {
              console.warn('Position CSV has no data rows');
              positionCache = map;
              resolve(map);
              return;
            }

            const headers = results.data[0];
            const mlbamIdIndex = headers.findIndex(h => 
              h.toLowerCase() === 'mlbamid' || h.toLowerCase() === 'mlbam_id'
            );
            const stdPosIndex = headers.findIndex(h => 
              h.toLowerCase() === 'std_pos' || h.toLowerCase() === 'stdpos'
            );
            const yahooPosIndex = headers.findIndex(h => 
              h.toLowerCase() === 'yahoo_pos' || h.toLowerCase() === 'yahoopos'
            );

            if (mlbamIdIndex === -1) {
              console.warn('Could not find MLBAMID column in position CSV');
              positionCache = map;
              resolve(map);
              return;
            }

            const posIndex = yahooPosIndex !== -1 ? yahooPosIndex : stdPosIndex;

            for (let i = 1; i < results.data.length; i++) {
              const row = results.data[i];
              const mlbamId = row[mlbamIdIndex]?.trim();
              const posStr = posIndex !== -1 ? row[posIndex]?.trim() : '';

              if (mlbamId && mlbamId.length > 0) {
                const positions = parsePositionString(posStr);
                map.set(mlbamId, positions);
              }
            }

            console.log(`Loaded ${map.size} player positions from CSV`);
            positionCache = map;
            resolve(map);
          },
          error: (error: Error) => {
            console.error('Failed to parse position CSV:', error);
            positionCache = new Map();
            resolve(positionCache);
          },
        });
      })
      .catch(error => {
        console.error('Failed to fetch position CSV:', error);
        positionCache = new Map();
        resolve(positionCache);
      });
  });

  return loadingPromise;
}

export async function lookupPlayerPositions(
  mlbamIds: string[],
  onProgress?: (completed: number, total: number) => void
): Promise<Map<string, string[]>> {
  const positionData = await loadPositionData();
  const results = new Map<string, string[]>();

  for (let i = 0; i < mlbamIds.length; i++) {
    const id = mlbamIds[i];
    const positions = positionData.get(id) || ['UTIL'];
    results.set(id, positions);

    if (onProgress && i % 100 === 0) {
      onProgress(i + 1, mlbamIds.length);
    }
  }

  if (onProgress) {
    onProgress(mlbamIds.length, mlbamIds.length);
  }

  return results;
}

export async function getPlayerPosition(mlbamId: string): Promise<string[]> {
  const positionData = await loadPositionData();
  return positionData.get(mlbamId) || ['UTIL'];
}

export function clearPositionCache(): void {
  positionCache = null;
  loadingPromise = null;
}

client/src/lib/projection-merger.ts.content:
import type { PlayerProjection } from '@shared/schema';

export interface MergeConflict {
  playerId: string;
  playerName: string;
  conflictType: 'duplicate' | 'stats_mismatch';
  resolution: string;
}

export interface MergeResult {
  mergedProjections: PlayerProjection[];
  conflicts: MergeConflict[];
  hittersCount: number;
  pitchersCount: number;
  dualPlayersCount: number;
}

function normalizePlayerName(name: string): string {
  return name
    .toLowerCase()
    .trim()
    .replace(/[.\-']/g, '')
    .replace(/\s+/g, ' ')
    .replace(/\bjr\b|\bsr\b|\bii\b|\biii\b|\biv\b/gi, '')
    .trim();
}

function normalizeTeamName(team: string | undefined): string {
  if (!team) return '';
  return team.toLowerCase().trim();
}

function isHitterPosition(positions: string[]): boolean {
  const hitterPositions = ['C', '1B', '2B', '3B', 'SS', 'OF', 'LF', 'CF', 'RF', 'DH', 'UTIL', 'MI', 'CI'];
  return positions.some(p => hitterPositions.includes(p.toUpperCase()));
}

function isPitcherPosition(positions: string[]): boolean {
  const pitcherPositions = ['SP', 'RP', 'P'];
  return positions.some(p => pitcherPositions.includes(p.toUpperCase()));
}

function mergePositions(pos1: string[], pos2: string[]): string[] {
  const combined = new Set<string>();
  pos1.forEach(p => combined.add(p.toUpperCase()));
  pos2.forEach(p => combined.add(p.toUpperCase()));
  return Array.from(combined);
}

function mergeStats(stats1: Record<string, number>, stats2: Record<string, number>): Record<string, number> {
  const merged: Record<string, number> = { ...stats1 };
  for (const [key, value] of Object.entries(stats2)) {
    if (merged[key] === undefined) {
      merged[key] = value;
    }
  }
  return merged;
}

function createPlayerKey(projection: PlayerProjection): string {
  if (projection.mlbamId) {
    return `mlbam:${projection.mlbamId}`;
  }
  const normalizedName = normalizePlayerName(projection.name);
  const normalizedTeam = normalizeTeamName(projection.team);
  return `name:${normalizedName}:${normalizedTeam}`;
}

export function mergeProjections(
  hitterProjections: PlayerProjection[],
  pitcherProjections: PlayerProjection[]
): MergeResult {
  const conflicts: MergeConflict[] = [];
  const playerMap = new Map<string, PlayerProjection>();

  for (const hitter of hitterProjections) {
    const key = createPlayerKey(hitter);
    playerMap.set(key, { ...hitter });
  }

  for (const pitcher of pitcherProjections) {
    const key = createPlayerKey(pitcher);
    const existing = playerMap.get(key);

    if (existing) {
      const mergedPositions = mergePositions(existing.positions, pitcher.positions);
      const mergedStats = mergeStats(existing.stats, pitcher.stats);

      playerMap.set(key, {
        ...existing,
        positions: mergedPositions,
        stats: mergedStats,
        mlbamId: existing.mlbamId || pitcher.mlbamId,
      });

      conflicts.push({
        playerId: existing.mlbamId || key,
        playerName: existing.name,
        conflictType: 'duplicate',
        resolution: 'Merged hitter and pitcher stats (two-way player)',
      });
    } else {
      playerMap.set(key, { ...pitcher });
    }
  }

  const mergedProjections = Array.from(playerMap.values());

  let hittersCount = 0;
  let pitchersCount = 0;
  let dualPlayersCount = 0;

  for (const projection of mergedProjections) {
    const isHitter = isHitterPosition(projection.positions);
    const isPitcher = isPitcherPosition(projection.positions);

    if (isHitter && isPitcher) {
      dualPlayersCount++;
    } else if (isHitter) {
      hittersCount++;
    } else if (isPitcher) {
      pitchersCount++;
    }
  }

  return {
    mergedProjections,
    conflicts,
    hittersCount,
    pitchersCount,
    dualPlayersCount,
  };
}

export function identifyPlayerType(projection: PlayerProjection): 'hitter' | 'pitcher' | 'two-way' {
  const isHitter = isHitterPosition(projection.positions);
  const isPitcher = isPitcherPosition(projection.positions);

  if (isHitter && isPitcher) return 'two-way';
  if (isPitcher) return 'pitcher';
  return 'hitter';
}
